function ajax(){ 
  var ajaxData = { 
    url:arguments[0].url || "", 
    data:arguments[0].data || null, 
    contentType:arguments[0].contentType || "application/x-www-form-urlencoded; charset=utf-8", 
    scriptCharset: 'utf-8',
    beforeSend:arguments[0].beforeSend || function(){}, 
    success:arguments[0].success || function(){}, 
    error:arguments[0].error || function(){} 
  } 
  ajaxData.beforeSend() 
  var xhr = createxmlHttpRequest();  
  xhr.responseType='JSON'; 
  xhr.open('POST',ajaxData.url,true);  
  xhr.setRequestHeader("Content-Type",ajaxData.contentType);  
  if(arguments[0].token){
    xhr.setRequestHeader("token",arguments[0].token);
  }
  xhr.send(convertData(ajaxData.data));  
  // xhr.send(JSON.stringify(ajaxData.data));  
  xhr.onreadystatechange = function() { 
    if (xhr.readyState == 4) {  
      var res=JSON.parse(xhr.response);
      if(xhr.status == 200||xhr.status==201&&res.error_code==0){ 
        ajaxData.success(res) 
      }else if(xhr.status==400){
        if(JSON.parse(xhr.response).error_code>0){
          toast(-1,JSON.parse(xhr.response).msg);
        }
      }else{ 
        ajaxData.error(xhr) 
      }  
    } 
  }  
}
function getajax(){ 
  var ajaxData = { 
    url:arguments[0].url || "", 
    data:arguments[0].data || null, 
    contentType:arguments[0].contentType || "application/x-www-form-urlencoded", 
    beforeSend:arguments[0].beforeSend || function(){}, 
    success:arguments[0].success || function(){}, 
    error:arguments[0].error || function(){} 
  } 
  ajaxData.beforeSend() 
  var xhr = createxmlHttpRequest();  
  xhr.responseType='JSON'; 
  var str=''
  console.log(ajaxData.data);
  for (var key in ajaxData.data){
    
            str+=key + '=' + ajaxData.data[key]+'&';
       }
       str=str.substring(0,str.length-1)
  xhr.open('GET',ajaxData.url+'?'+str,true);  
  xhr.setRequestHeader("Content-Type",ajaxData.contentType);  
  if(arguments[0].token){
    xhr.setRequestHeader("token",arguments[0].token);
  }
  xhr.send(null);
  xhr.onreadystatechange = function() {  
    console.log(xhr.response);
    if (xhr.readyState == 4) {  
      if(xhr.status == 200){ 
        ajaxData.success(JSON.parse(xhr.response)) 
      }else if(xhr.status==404){
        if(JSON.parse(xhr.response).error_code==10001){
          console.log('aaaaaa');
        }
      }else{ 
        ajaxData.error(xhr);
      }  
    } 
  }  
} 
  
function createxmlHttpRequest() {  
  if (window.ActiveXObject) {  
    return new ActiveXObject("Microsoft.XMLHTTP");  
  } else if (window.XMLHttpRequest) {  
    return new XMLHttpRequest();  
  }  
} 
  
function convertData(data){ 
  if( typeof data === 'object' ){ 
    var convertResult = "" ;  
    for(var c in data){  
      if(c=='content'||c=='details'){
        convertResult+= c + "=" + encodeURIComponent(data[c]) + "&";  
      }else{
        convertResult+= c + "=" + data[c] + "&";  
      }
    }  
    convertResult=convertResult.substring(0,convertResult.length-1) 
    return convertResult; 
  }else{ 
    return data; 
  } 
}
