function changeDay(now) {
	// now:new Date()
	var year = now.getFullYear();
	var month = now.getMonth() + 1;
	var date = now.getDate();
	var hour = now.getHours();
	var minute = now.getMinutes();
	var second = now.getSeconds();
	if (parseInt(month) < 10) {
		month = '0' + month
	}
	if (parseInt(date) < 10) {
		date = '0' + date
	}
	return year + "-" + month + "-" + date + " " + "00:00:00"
}

function chartchangeDay(now) {
	var year = now.getFullYear();
	var month = now.getMonth() + 1;
	var date = now.getDate();
	var hour = now.getHours();
	var minute = now.getMinutes();
	var second = now.getSeconds();
	month = parseInt(month) > 10 ? month : ('0' + month)
	date = parseInt(date) > 10 ? date : ('0' + date)
	if (parseInt(hour) < 10) {
		hour = '0' + hour
	}
	if (parseInt(minute) < 10) {
		minute = '0' + minute
	}
	if (parseInt(second) < 10) {
		second = '0' + second
	}
	return year + "-" + month + "-" + date //+ " " + hour + ":" + minute + ":" + second;
}


function changeday(time) {
	var date = new Date(time); 
	Y = date.getFullYear() + '-';
	M = (date.getMonth()+1 < 10 ? '0'+(date.getMonth()+1) : date.getMonth()+1) + '-';
	D = date.getDate() < 10 ? '0' + date.getDate() +' ' : date.getDate()+ ' ';
	h = (date.getHours() < 10 ? '0'+(date.getHours()) : date.getHours())  + ':';
	m = (date.getMinutes() < 10 ? '0' + date.getMinutes() : date.getMinutes()) + ':';
	s = date.getSeconds() < 10 ? '0' + date.getSeconds() : date.getSeconds(); 
	showTime = Y+M+D;
	return showTime;
}
function changetime(time) {
	var date = new Date(time); 
	Y = date.getFullYear() + '-';
	M = (date.getMonth()+1 < 10 ? '0'+(date.getMonth()+1) : date.getMonth()+1) + '-';
	D = date.getDate() < 10 ? '0' + date.getDate() +' ' : date.getDate()+ ' ';
	h = (date.getHours() < 10 ? '0'+(date.getHours()) : date.getHours())  + ':';
	m = (date.getMinutes() < 10 ? '0' + date.getMinutes() : date.getMinutes()) + ':';
	s = date.getSeconds() < 10 ? '0' + date.getSeconds() : date.getSeconds(); 
	showTime = Y+M+D+h+m+s;
	return showTime;
}

function formatDate(now) {
	var year = now.getFullYear();
	var month = now.getMonth() + 1;
	var date = now.getDate();
	var hour = now.getHours();
	var minute = now.getMinutes();
	var second = now.getSeconds();
	month = parseInt(month) >= 10 ? month : ('0' + month)
	date = parseInt(date) >= 10 ? date : ('0' + date)
	if (parseInt(hour) < 10) {
		hour = '0' + hour
	}
	if (parseInt(minute) < 10) {
		minute = '0' + minute
	}
	if (parseInt(second) < 10) {
		second = '0' + second
	}
	return year + "-" + month + "-" + date + " " + hour + ":" + minute + ":" + second;
}

function checkIntNum(obj) {
	//检查是否是非数字值
	if (isNaN(obj.value)) {
		obj.value = "";
	}
	var $parent = $(obj).parent();
	if (obj != null) {
		//检查不能是小数
		if (obj.value.toString().split(".").length > 1) {
			var errorMsg = '必须为整数.';
			$parent.append('<p class="formtips onError">' + errorMsg + '</p>');
			obj.value = "";
			$('.onError').fadeOut(3000);
			return false;
		} else {}
	}
};

function checkNum(obj) {
	//检查是否是非数字值
	if (isNaN(obj.value)) {
		obj.value = "";
	}
	var $parent = $(obj).parent();
	if (obj != null) {
		//检查小数点后是否对于两位
		if (obj.value.toString().split(".").length > 1 && obj.value.toString().split(".")[1].length > 2) {
			var errorMsg = '小数点后不能超过两位.';
			$parent.append('<p class="formtips onError">' + errorMsg + '</p>');
			obj.value = "";
			$('.onError').fadeOut(3000);
			return false;
		} else {}
	}
};

function checkHour(obj) {
	//检查是否是非数字值
	if (isNaN(obj.value)) {
		obj.value = "";
	}
	var $parent = $(obj).parent();
	if (obj != null) {
		//检查小数点后是否对于两位
		if (obj.value.toString().split(".").length > 1 && obj.value.toString().split(".")[1].length >= 0) {
			var errorMsg = '不能为小数.';
			$parent.append('<p class="formtips onError">' + errorMsg + '</p>');
			obj.value = "";
			$('.onError').fadeOut(3000);
			return false;
		} else {}
	}
};
